const noflo = require('../../../../../../src/lib/NoFlo');

exports.getComponent = function () {
  const c = new noflo.Component();
  c.description = 'Forward stuff';
  c.inPorts.add('in',
    { datatype: 'all' });
  c.inPorts.add('out',
    { datatype: 'all' });
  c.process = function (input, output) {
    const data = input.getData('in');
    output.sendDone({ out: data });
  };
  return c;
};
