const noflo = require('../../../../../src/lib/NoFlo');

module.exports = function(loader, callback) {
  loader.registerComponent('example', 'Hello', function() {
    const c = new noflo.Component;
    c.description = "Hello stuff";
    c.icon = 'bicycle';
    c.inPorts.add('in',
      {datatype: 'all'});
    c.inPorts.add('out',
      {datatype: 'all'});
    c.process = function(input, output) {
      const data = input.getData('in');
      output.sendDone({
        out: data});
    };
    return c;
  });
  callback(null);
};
